const stores = require('../../resources/shopify/stores.json');

const validStores = stores.map((item) => item.id);
const validLanguages = stores
  .map((item) => item.locales)
  .flat()
  .reduce(
    (prev, curVal) => (prev.includes(curVal) ? prev : prev.concat(curVal)),
    []
  );

module.exports = { validStores, validLanguages };
