const { ProductSchema } = require('../src');

describe('module', () => {
  test('should expose Product and child Schemas', () => {
    expect(ProductSchema).toHaveProperty('Product');
    expect(ProductSchema).toHaveProperty('image');
    expect(ProductSchema).toHaveProperty('language');
    expect(ProductSchema).toHaveProperty('releaseDate');
    expect(ProductSchema).toHaveProperty('variant');
    expect(ProductSchema).toHaveProperty('tag');
    expect(ProductSchema).toHaveProperty('active');
    expect(ProductSchema).toHaveProperty('langBasedNullableSchema');
    expect(ProductSchema).toHaveProperty('colorSwatch');
    expect(ProductSchema).toHaveProperty('careInstruction');
    expect(ProductSchema).toHaveProperty('ingredient');
    expect(ProductSchema).toHaveProperty('material');
    expect(ProductSchema).toHaveProperty('measurementImage');
    expect(ProductSchema).toHaveProperty('measurement');
    expect(ProductSchema).toHaveProperty('material');
  });
});
